import { combineReducers, Reducer } from 'redux';
import { FETCH_CATEGORIES, FETCH_CAT_IMAGES, SELECT_CATEGORY } from './actions';
import { AppAction, FetchCategoriesAction, FetchCatImagesAction, SelectCategoryAction } from './actions'; 
import { Category, CatImage } from '../models'; 

const categoriesReducer: Reducer<Category[], FetchCategoriesAction> = (
    state = [],
    action
  ) => {
  switch (action.type) {
    case FETCH_CATEGORIES:
      return action.payload;
    default:
      return state;
  }
};

const catImagesReducer: Reducer<CatImage[], FetchCatImagesAction> = (
  state = [],
  action
  ) => {
  switch (action.type) {
    case FETCH_CAT_IMAGES:
      return action.payload;
    default:
      return state;
  }
};

const selectedCategoryReducer: Reducer<number | null, SelectCategoryAction> = (
  state = null,
  action
) => {
  switch (action.type) {
    case SELECT_CATEGORY:
      return action.payload;
    default:
      return state;
  }
};

export const rootReducer = combineReducers({
  categories: categoriesReducer,
  catImages: catImagesReducer,
  selectedCategory: selectedCategoryReducer,
});

export type RootState = ReturnType<typeof rootReducer>;
export default rootReducer;
